package com.seitenbau.testdatadsl.dbunitdemo.groovyimport org.dbunit.dataset.DataSetException;import org.dbunit.dataset.IDataSet;import com.seitenbau.testdatadsl.dbunitdemo.IDataSetProvider

class GroovyOpOverloadingDataSetProvider implements IDataSetProvider{  HochschuleTableModel model = new HochschuleTableModel()  def GroovyOpOverloadingDataSetProvider() {    HochschuleTableParser parser = new HochschuleTableParser()    def table = parser.asListOfRows {          professorTable.rows {        REF    | name    | vorname  | titel            | fakultaet    | leitet               | beaufsichtigt        WAESCH | "Wäsch" | "Jürgen" | "Prof. Dr.-Ing." | "Informatik" | _                    | P_VSYS        HAASE  | "Haase" | "Oliver" | "Prof. Dr."      | "Informatik" | VSYS & DPATTERNS     | P_DPATTERNS      }          lehrveranstaltungTable.rows {        REF       | name                | sws | ects        VSYS      | "Verteilte Systeme" | 4   | 5        DPATTERNS | "Design Patterns"   | 4   | 3      }          pruefungTable.rows {        REF         | lehrveranstaltung | typ   | zeitpunkt        P_VSYS      | VSYS              | "K90" | "2013-04-01 14:00:00"        P_DPATTERNS | DPATTERNS         | "M30" | "2013-01-06 12:00:00"      }          studentTable.rows {        matrikelnummer | name         | vorname    | studiengang | semester | immatrikuliertSeit | besucht   | istTutor | schreibt        287336         | "Moll"       | "Nikolaus" | "MSI"       | 4        | "2011-09-01"       | VSYS      | VSYS     | P_VSYS        123456         | "Mustermann" | "Max"      | "BIT"       | 3        | "2012-03-01"       | DPATTERNS | _        | _      }    }    model.apply(table)  }    public IDataSet getDataSet() throws DataSetException  {    return model.createDBUnitDataSet()  }

}
